class ContenuRubrique extends BaseContenuRubrique {

    public function __toString() {
        return $this->getFrTitre();
    }

    public function getListeContenuLie() {

        $contenuLie = Doctrine_Query::create()
                ->from('Contenu C')
                ->where('C.id_rubrique_parente  = ?', $this->getIdRubrique())
                ->execute();

        return $contenuLie;
    }

}
