<?php

/**
 * GroupeStatTable
 * 
 * This class has been auto-generated by the Doctrine ORM Framework
 */
class GroupeStatTable extends Doctrine_Table
{
    /**
     * Returns an instance of this class.
     *
     * @return object GroupeStatTable
     */
    public static function getInstance()
    {
        return Doctrine_Core::getTable('GroupeStat');
    }


    /*
     * filtrage des groupe en fonction des sites
     *  retourne la liste des groupe du site et dont l'etat = 1
     *
     * @return object GroupeStatTable
     */
    public function getGroupeStatSite(Doctrine_Query $q = null) {
        if (is_null($q)) {
            $q = Doctrine_Query::create()
            ->from('GroupeStat gs');
        };
        $q->where('gs.etat = 1');

        $q->andWhere('gs.id_site = ?', sfConfig::get('sf_id_site_client'));

        return $q;
    }
}