<?php

/**
 * LivreOrTable
 * 
 * This class has been auto-generated by the Doctrine ORM Framework
 */
class LivreOrTable extends Doctrine_Table
{
    /**
     * Returns an instance of this class.
     *
     * @return object LivreOrTable
     */
    public static function getInstance()
    {
        return Doctrine_Core::getTable('LivreOr');
    }
    
    /*
     * filtrage des commentaires "Livre d'Or" en fonction du site (id_site)
     */
    public function getLivreOrSite(Doctrine_Query $q = null) {
        if (is_null($q)) {
            $q = Doctrine_Query::create()
            ->from('LivreOr lo');
        }

        $q->andWhere('lo.id_site_client = ?', sfConfig::get('sf_id_site_client'));

        return $q;
    }
}